import React, {useState, useEffect} from 'react'
import './App.css';




function App() {

  const [[keyword,interval,startDate,endDate], setMessage] = useState('');

  const handleChange = event => {
    setMessage(event.target.value);

    console.log('value is:', event.target.value);
  };

  //React.render(element);


  return (

    <form action="/">

    <h1 > Enter keyword(s) (Separate by spaces)</h1>
<input type="text" name="keyword" id="content" onChange={handleChange}
        value={keyword}></input>
<form action="/" >
    <h1  > Enter interval</h1>
<input type="text" name="interval" id="content" onChange={handleChange}
        value={interval}></input>
</form>
<form action="/">
    <h1 > Enter start date</h1>
<input type="text" name="startDate" id="content" onChange={handleChange}
        value={startDate}></input>
</form>
<form action="/" >
    <h1 > Enter end date</h1>
<input type="text" name="endDate" id="content" onChange={handleChange}
        value={endDate}></input>
<input type="submit" name="update" value = "Update"></input>
</form>
<form action="/">
    <h1> Enter # of books</h1>
<input type="text" name="numBooks" id="content"></input>
<input type="submit" name="update" value = "Update"></input>
</form>
<form action="/" >
    <h1> (Optional) Enter extra keywords </h1>
<input type="text" name="extraWords" id="content"></input>
<input type="submit" name="update" value = "Update"></input>
</form>
<form action="/" >
    <h1> (Optional) Enter analysis type (m-magazines, n-newspapers, b-books)</h1>
<input type="text" name="extraWords" id="content"></input>
<input type="submit" name="update" value = "Update"></input>
</form>
<form action="/" method="POST"></form>
<input type="submit" name="update" value = "Submit"></input>
<h1>Keywords: {keyword}</h1>
</form>

  );
}

export default App;
